import React, { useState } from 'react';
import { Container, Row, Col, Form, FormGroup, Label, Input, Button } from 'reactstrap';

import './contact.css';
import emailImg from '../../assets/images/email.png';
import mapImg from '../../assets/images/map.png';

const Contact = () => {

    const [formData, setFormData] = useState({
      name: '',
      email: '',
      message: '',
    });
  
    const handleSubmit = (event) => {
      event.preventDefault();
      const { name, email, message } = formData;
      const subject = `New contact form submission from ${name}`;
      const body = `Name: ${name}\nEmail: ${email}\nMessage: ${message}`;
      const mailtoLink = `mailto:adeebnasser@gmail.com?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
      window.location.href = mailtoLink;
    };
  
    const handleInputChange = (event) => {
      const { name, value } = event.target;
      setFormData({
        ...formData,
        [name]: value,
      });
    };
    
  return (
    <div className='contact' id='contact'>
      <Container>
        <Row>
          <Col>
            <h5>CONTACT</h5>
            <h4>
              Please do not hesitate to contact me. I am readily available to
              assist you.
            </h4>
            <div className='contact__links'>
              <div className='location'>
                <a
                  href='https://goo.gl/maps/qL5hXrYXCR4ogpjA9'
                  target='_blank'
                  rel='noopener noreferrer'
                >
                  <img src={mapImg} alt='location' />
                </a>
                <div className='location__text'>
                  <h6>Location</h6>
                  <a
                    href='https://goo.gl/maps/cAdR9N19rUeJvv8N6'
                    target='_blank'
                    rel='noopener noreferrer'
                  >
                    Austin, TX
                  </a>
                </div>
              </div>
              <div className='email'>
                <a href='mailto:adeebnasser@gmail.com'>
                  <img src={emailImg} alt='email' />
                </a>
                <div className='email__text'>
                  <h6>E-mail</h6>
                  <a href='mailto:adeebnasser@gmail.com'>
                    adeebnasser@gmail.com
                  </a>
                </div>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Contact;
